import React, {useContext, useState} from 'react';
//importing use context from MovieContext.js 
import { Form, Container, Col} from 'react-bootstrap';
import { HiPlus } from "react-icons/hi";
import {MovieContext} from './MovieContext';


const AddMovie = () => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    //adding movie context to add movie feature
    const [movies, setMovies] = useContext(MovieContext);

    const updateName = (e) =>
        setName(e.target.value);

    const updatePrice = (e) =>
        setPrice(e.target.value);

    const addMovie = e => {
        e.preventDefault();
        setMovies(prevMovies => [ ...prevMovies, {name: name, price: price}]);
        //movie list is an array with fields therefore we make an array for setMovies
    }

    return(
        //add a onsubmit button
        <div className="section-content">
        <form >
        <input type="text" name="name" />
        <input 
            type="text"
             name="price"  
             value={price}
              onChange={updatePrice} 
        />
        </form>

        <div className="App">
        <div className="phone-book-header">
        <img id="mail-icon" src="/images/pngtree-email-icon-png.jpg" alt="mail-icon"></img>
         <h1 className="phone-book-title">My UT Phone Book</h1>
         </div>
         <Container>
         <div className="new-contact-title"> <h4>Add New Contact</h4></div>
             <Form className="contact-name-border">
         <Form.Row>
           <Col>
             <Form.Control value={name} onChange={updateName} name="namer"  type="text" placeholder="Name" />
           </Col>
           <Col className="phone-column">
             <Form.Control value={price} onChange={updatePrice} type="text" placeholder="Phone Number" />
             </Col>
             
             <button onSubmit={addMovie} type="submit" className="add-button">
             <HiPlus className="plus-icon" /> <h4><div className="add-button-spacing">Add </div></h4>
             </button>
         </Form.Row>
       </Form>
       </Container>
       </div>

        </div>
        

    );
};

export default AddMovie;
